void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // set uv into the center of screen
    uv = fragCoord/iResolution.y - vec2(0.5*(iResolution.x/iResolution.y), 0.5);

	// set a dark blue color    
    vec3 col = vec3(0.01,0.1,0.4);


    // create a vignette effect with pow 
    col /= pow(0.76+length(uv), 1.40);																	

    // Output to screen
    fragColor = vec4(col,1.0);
}
