const float TAO = 2.0 * 3.14159265359;

#define SOUND_CHOICE 1 // 0 or 1


float tone(float t, float hz) {
    float T = t * TAO;

    return sin(T * hz);
}

vec2 mainSound( in int samp,float t) {

    vec2 v = vec2(0.0);


    #if SOUND_CHOICE == 0
    v  = vec2(tone(t, 300.0));
    #elif SOUND_CHOICE == 1
    v  = vec2(tone(fract(t), 300.0));
    #else
    #error no such option
    #endif

    v *= smoothstep(0.0, 1.0, t);

    // some attenuation to make sure it's not just clipping somehow.
    v *= 0.9;

    return v;
}
