#define SHARPEN_FACTOR 8.0

vec4 unsharpenMask (sampler2D tex, vec2 fragCoord)
{
    // Sharpen detection matrix [0,1,0],[1,-4,1],[0,1,0]
    // Colors
    vec4 up_left = texture (tex, (fragCoord + vec2 (-1, 1))/iResolution.xy);
    vec4 up = texture (tex, (fragCoord + vec2 (0, 1))/iResolution.xy);
    vec4 up_right = texture (tex, (fragCoord + vec2 (1, 1))/iResolution.xy);
    vec4 left = texture (tex, (fragCoord + vec2 (-1, 0))/iResolution.xy);
    vec4 center = texture (tex, fragCoord/iResolution.xy);
    vec4 right = texture (tex, (fragCoord + vec2 (1, 0))/iResolution.xy);
    vec4 down_left = texture (tex, (fragCoord + vec2 (-1, -1))/iResolution.xy);
    vec4 down = texture (tex, (fragCoord + vec2 (0, -1))/iResolution.xy);
    vec4 down_right = texture (tex, (fragCoord + vec2 (1, -1))/iResolution.xy);

    vec4 blur = 0.0625*up_left + 0.125*up + 0.0625*up_right + 0.125*left + 0.25*center + 0.125*right + 0.0625*down_left + 0.125*down + 0.0625*down_right;

    // Return edge detection
    return center + (center - blur) * SHARPEN_FACTOR;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Detect edges and output to screen
    fragColor = unsharpenMask (iChannel0, fragCoord);
}
