vec2 h(vec2 p) // Dave H
{
    p = mod(p,N);
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}
float ln (vec2 U, vec2 c, vec2 d) {
    U = U-c-(d-c)*clamp(dot(U-c,d-c)/dot(d-c,d-c),0.0,1.);
	return dot(U,U);
}
void mainImage( out vec4 Q, vec2 U )
{
    if (U.x>N||U.y>N)discard;
	else
    {
    vec2 u = floor(U/O+0.5)*O;
    float l = 1e3;
    Q = vec4(0);
    #define W 6
    for (int i=0;i<W*W;i++){
    for (int j=i+1;j<W*W;j++){
        vec2 c = u + O*vec2(ivec2(i%W,i/W)-W/2), d = u + O*vec2(ivec2(j%W,j/W)-W/2);
        d += O*sin((6.*iTime+100.)*h(d));
        c += O*sin((6.*iTime+100.)*h(c));
        Q += exp(-3./float(W)*dot(d-c,d-c)/O/O)*exp(-2.5*ln(U, c , d));
    }
    }
    }
}
