float r(float p, float f)
{
	return smoothstep(0.,f,length(p)-.15)+(1.-smoothstep(0.,f,length(p)-.1) );
}
void mainImage( out vec4 c, in vec2 u )
{
    u = (2.*u-iResolution.xy)/iResolution.y;
    float l = length(vec2(u.x+(sin(iTime)),u.y)),
        p =length(vec2(u.x,u.y)),
    	f = (1.1+cos(iTime*2.))*l/12.;
    c = vec4(vec3(r(l,.01) * (r(p/4.,f*p))),1.0);
}
