#define S(a, b, t) smoothstep(a, b, t)


float N21(vec2 p) {
	vec3 a = fract(vec3(p.xyx) * vec3(213.897, 653.453, 253.098));
    a += dot(a, a.yzx + 79.76);
    return fract((a.x + a.y) * a.z);
}

vec2 N22(vec2 p) {
	float n = N21(p);
    return vec2(n,N21(p+n));
}

vec2 GetPos(vec2 id, vec2 offset) {
    vec2 n = N22(id+offset)*iTime;
    return offset+sin(n)*.4;
}

float DistLine(vec2 p,vec2 a, vec2 b){
 	vec2 ap = p-a;
    vec2 ab = b-a;
    float t = clamp(dot(ap,ab)/dot(ab,ab),0., 1.);
    return length(ap - ab*t);
}

float Line(vec2 p, vec2 a, vec2 b){
 	float d = DistLine(p,a,b);
    float m = S(.03,.01,d);
    m*=S(1.2,.8,length(a-b));
    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
    uv *= 5.;

    vec2 coords = fract(uv)-.5;
    vec2 id = floor(uv);

    //vec2 point = GetPos(id);
    //float d = length(coords - point);
    float m = 0.;

    vec2 p[9];

    int i =0;
    for(float y=-1.;y<=1.;y++){
        for(float x=-1.;x<=1.;x++){
     		p[i++]=GetPos(id,vec2(x,y));
        }
    } 

    for(int i=0;i<9;i++){   
    	m += Line(coords,p[4],p[i]);

        vec2 j = (p[i]-coords)*20.;
        float sparkle = 1./dot(j,j);
        m+=sparkle*(sin(iTime*8.+p[i].x*10.)*.5+.5);
    }

    m+=Line(coords,p[1],p[3]);
    m+=Line(coords,p[1],p[5]);
    m+=Line(coords,p[3],p[7]);
    m+=Line(coords,p[5],p[7]);

    m+=Line(coords,p[i],p[(i+2)%9]);

    vec3 col = vec3(m);
    //if(coords.x>.48 || coords.y>.48) col = vec3(1,0,0);

    fragColor = vec4(col,1);
}
