#define a1 1.8
#define b1 0.2
#define c1 0.0
#define width 0.001

// y = ax^2 + bx + c
float drawLine(vec2 uv, float a, float b, float c) {
    vec2 line = vec2(uv.x, a*uv.x*uv.x+b*uv.x + c);
    return step(distance(uv, line), width);    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x;
    float line = drawLine(uv,a1,b1, c1);

    // Output to screen
    fragColor = vec4(line);
}
