vec4 OutLineRectangel(vec2 st, vec2 center, float width, float hight, float outline, vec3 colorOutLine)
{
    float left = step(center.x - width ,st.x) * step(1.0- (center.x - width + outline),1.0 - st.x) 
        * step(1.0 -(center.y + hight ),1.0-st.y)* step((center.y - hight),st.y);
    float right = step(1.0-(center.x + width ),1.0-st.x) * step(center.x + width- outline,st.x)
        * step(1.0 -(center.y + hight ),1.0-st.y)* step((center.y - hight ),st.y);
    float bottom = step(center.y - hight,st.y) * step(1.0- (center.y - hight+ outline),1.0 -st.y)
        * step(1.0 -(center.x + width),1.0-st.x)* step((center.x - width),st.x);
    float top = step(1.0-(center.y + hight ),1.0-st.y) * step(center.y + hight- outline,st.y)
        * step(1.0 -(center.x + width),1.0-st.x)* step((center.x - width),st.x);
    float alpha = left + bottom  + top + right;
	return vec4(colorOutLine,alpha);
}

vec4 rectangel(vec2 st, vec2 center, float width, float hight,inout vec3 color, float outline, vec3 colorOutLine)
{
     float left = step(center.x - width,st.x);
     float right = step(1.0-(center.x + width),1.0-st.x);
     float bottom = step(center.y - hight,st.y);
     float top = step(1.0-(center.y + hight),1.0-st.y);

    float alpha = left * bottom *top *right;
    vec4 outlineColor = OutLineRectangel(st,center,width,hight,outline,colorOutLine);
    color = mix(color,vec3(outlineColor.x,outlineColor.y,outlineColor.z),outlineColor.a);
	return vec4(color,alpha);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;

    vec4 colorBG = vec4(0.000);
    vec3 colorRect = vec3(1.0);
    vec3 colorRectOL = vec3(0.001,0.047,1.000);
    vec4 color = rectangel(st,vec2(0.5,0.5), 0.2 , 0.2,colorRect,0.02,colorRectOL);
    colorBG = mix(colorBG,color,color.a);
    color = rectangel(st,vec2(0.2,0.8), 0.2 , 0.2,colorRect,0.02,colorRectOL);
    colorBG = mix(colorBG,color,color.a);
    fragColor = colorBG;
}
