void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv.x *= iResolution.x/iResolution.y;
    vec2 sc = vec2(sin(iTime), cos(iTime));
    float k = length(uv)*0.15 - 0.01*sc.x - 0.05;
    for (float i = 0.; i < 6.; i++) {
        uv = abs(uv) + k;
        uv *= mat2(sc.x, -sc.y, sc.y, sc.x);
    }
    uv = pow(uv, vec2(0.3 + cos(iTime/3.)*0.05));
    fragColor = vec4(uv, pow(uv.y,uv.x), 1.);
}
