Main {
	Q = D(C(U).xy);
    float 
        n = length(A(U+vec2(0,1))),
        e = length(A(U+vec2(1,0))),
        s = length(A(U-vec2(0,1))),
        w = length(A(U-vec2(1,0)));
    vec3 norm = 
        normalize(vec3(e-w,n-s,.01)),
        ref = reflect(vec3(0,0,-1),norm);
   	vec3 l = R.xyy;
    float li = ln(vec3(U,0),vec3(U,0)+ref,l);
    Q *= .5+0.5*exp(-li)+.5*exp(-3.*li);
    if (abs(U.y-0.5*R.y)<3.) Q *= 0.;
    //Q = A(U);

    //Q = 0.5+0.5*(abs(Q.x*Q.w-Q.z*Q.y)*vec4(1)-1.);

}

vec4 Aa (vec2 U) {
	return A(U-B(U).xy);
}
vec4 Bb (vec2 U) {
	return B(U-B(U).xy);
}
Main {
	Q = Aa(U);
    vec4
        n = Bb(U+vec2(0,1)),
        e = Bb(U+vec2(1,0)),
        s = Bb(U-vec2(0,1)),
        w = Bb(U-vec2(1,0)),
        dx = 0.25*(e-w),
        dy = 0.25*(n-s);
    mat2 m = mat2(Q);
    m -= .25*mat2(
    	dx.x, dx.y,
        dy.x, dy.y
    )*m;

    Q = vec4(m);
	if (iFrame < 1) Q = vec4(1,0,0,1);
    if(U.x<3.||U.y<3.||R.x-U.x<3.||R.y-U.y<3.) Q.yz *= 0.;

}

vec4 Aa (vec2 U) {
	return A(U-A(U).xy);
}
vec4 Bb (vec2 U) {
	return B(U-A(U).xy);
}
Main {
	Q = Aa(U);
    vec4
        n = Bb(U+vec2(0,1)),
        e = Bb(U+vec2(1,0)),
        s = Bb(U-vec2(0,1)),
        w = Bb(U-vec2(1,0)),
        dx = 0.25*(e-w),
        dy = 0.25*(n-s);

    Q.x -= (e.x*e.w-e.y*e.z)-(w.x*w.w-w.y*w.z);
    Q.y -= (n.x*n.w-n.y*n.z)-(s.x*s.w-s.y*s.z);
    if (U.y>0.5*R.y) {
    	Q.x -= (n.x*n.z-n.y*n.w)-(s.x*s.z-s.y*s.w);
    	Q.y += (e.x*e.z-e.y*e.w)-(w.x*w.z-w.y*w.w);
	}
    if (iFrame < 1) {
    	Q = vec4(0);
       	if (length(U-0.5*R)<100.) 
            Q.xy = vec2(.5,0);
    }
    vec4 d = D(U);
        if ((iMouse.z>0.)&&ln(U,d.xy,d.zw)<4.)
            Q = vec4(clamp(.1*(d.xy-d.zw),-.8,.8),0,0);
    if (abs(U.y-0.5*R.y)<3.) Q *= 0.;

    if(U.x<3.||U.y<3.||R.x-U.x<3.||R.y-U.y<3.) Q *= 0.;
}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)

#define Main void mainImage (out vec4 Q, in vec2 U)

float ln (vec2 p, vec2 a, vec2 b) { // returns distance to line segment for mouse input
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}float ln (vec3 p, vec3 a, vec3 b) { // returns distance to line segment for mouse input
    return length(p-a-(b-a)*dot(p-a,b-a)/dot(b-a,b-a));
}

Main {
    Q = mod(C(U-B(U).xy),R.xyxy);

	if (iFrame < 1) Q = vec4(U,0,0);
}

// keep track of mouse
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 p = texture(iChannel0,fragCoord/iResolution.xy);
    if (iMouse.z>0.) {
        if (p.z>0.) fragColor =  vec4(iMouse.xy,p.xy);
    	else fragColor =  vec4(iMouse.xy,iMouse.xy);
    }
    else fragColor = vec4(-iResolution.xy,-iResolution.xy);
}
