vec2 uv;
vec3 col;

float pres = 0.005;

float offset = 0.01;

float l = 10.0;

void doLine(vec2 a,vec2 b,vec3 c)
{
    vec2 diff = a-b;

    if(diff.x != 0.0)
    {

    float m = diff.y/diff.x;
    float t = a.y - m * a.x;

        if(uv.y >= uv.x*m+t-pres && uv.y <= uv.x*m+t+pres)
        {
            if(uv.x<= max(a.x,b.x)&&uv.y<=max(a.y,b.y)  && uv.x>=min(a.x,b.x)&&uv.y>=min(a.y,b.y))
            {

             col =c;

            }

        }

    }
    else
    {
       if(uv.x>= a.x-pres && uv.x<= a.x+pres)
        {
            if(uv.y<=max(a.y,b.y)&&uv.y>=min(a.y,b.y))
            {

             col = vec3(1);

            }

        }   
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //l = float(int(iTime/3.0)+1);

    uv = ((fragCoord-.5*iResolution.xy)/iResolution.y)*2.5;
    uv.x+= 1.5;


    float nTime = iTime;

    vec2 start = vec2(0);
    vec2 end = vec2(0);

    for(float i = 1.0; i<=l; i++)
    {
        vec2 nstart = end;

         end = end + (1.0/(i*3.0))*vec2(cos((nTime)*i*3.0),sin((nTime)*i*3.0));
        doLine(nstart,end,vec3(1,0,0));

        start = nstart;
    }



    for(float i = 0.0; i<=300.0; i++)
    {


        nTime = iTime-offset*i;
        vec2 start = vec2(0);
        vec2 end = vec2(0);


         for(float n = 1.0; n<=l; n++)
    	{
        vec2 nstart = end;

        end = end + (1.0/(n*3.0))*vec2(cos((nTime)*n*3.0),sin((nTime)*n*3.0));

            if(n>=l)
            {
        doLine(vec2(i*offset+1.0,nstart.y),vec2(i*offset+1.0,end.y),vec3(1));

        if(i <1.0)
        doLine(vec2(end.x,end.y+0.01),vec2(i*offset+1.0,end.y),vec3(0,0,1));
            }
        start = nstart;
    }

    }


    fragColor = vec4(col,1.0);
}
