#define PI 3.141592
#define TAU PI * 2.

const vec4 colorMain = vec4(1., 0.9, 0.1, 1.);
const vec4 colorBlack = vec4(0);
const vec4 colorWhite = vec4(1);

float from(float x, float min, float max) {
    return (x - min) / (max - min);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 light = vec3(cos(iTime), .0, sin(iTime));
    vec2 ratio = vec2(iResolution.x / iResolution.y, 1.);
    vec2 pos = fragCoord / iResolution.xy;
    vec2 npos = 2. * pos - 1.;
    vec2 cpos = npos * ratio;
    float apos = atan(cpos.y, cpos.x);
    vec3 norm = normalize(vec3(cpos, sqrt(1. - dot(cpos, cpos))));
    vec2 tpos = vec2(
      atan(norm.z, norm.x) / TAU,
      asin(norm.y) / TAU
    );

    vec2 tdpos0 = vec2(
        mod(iTime * .1, 1.),
        mod(-iTime* 0.01, 1.)
    ) + tpos;
    vec4 tex0 = texture(
      iChannel0,
      tdpos0
    );

    vec2 tdpos1 = vec2(
        mod(iTime * .1, 1.),
        mod(iTime * 0.01, 1.)
   	) + tpos;
    vec4 tex1 = texture(
        iChannel1,
        tdpos1
    );

    float rx = atan(norm.y, norm.x) / PI;
    float ry = mod(iTime * 0.01, 1.);
    vec2 rpos = vec2(rx, ry);
    vec4 rtex0 = texture(
        iChannel0,
        rpos
    );
    vec4 rtex1 = texture(
        iChannel1,
        rpos
    );

    float incidence = dot(light, norm);
    float dist = length(cpos);
    float ndist = 1. - dist;
    if (dist > 1.0) {
        fragColor = vec4(0);
        fragColor += mix(
            colorMain,
            colorBlack,
            pow(dist - 1., .25)
        );

        float ldist = 1.0 + rtex1.x * .75;
        if (dist < ldist) {
            vec4 corona = mix(
                colorMain * 0.1,
                colorBlack,
                from(dist, 1.0, ldist)
            );
            fragColor += corona;
        }

        float hdist = 1.0 + rtex0.x * 0.05 + rtex1.x * 0.1;
        if (dist < hdist) {
            vec4 corona = mix(
                colorMain * 0.5,
                colorBlack,
                from(dist, 1.0, hdist)
            );
            fragColor += corona;
        }

        float rdist = 1.0 + rtex0.x * 0.1;
        if (dist < rdist) {
            vec4 corona = mix(
                colorMain + vec4(0.5, 0.5, 0.5, 1.),
                colorBlack,
                from(dist, 1.0, rdist)
            );
            fragColor += corona;
        }
    } else {
    	fragColor = vec4(
            mix(
                colorWhite * 0.1,
                colorMain,
            	(tex0.x + tex1.x) * 0.5
            )
        ) * ndist;

        fragColor += vec4(
            mix(
                colorMain,
                colorBlack,
                pow(ndist, 0.8)
            )
        );

        fragColor += vec4(
            mix(
                colorMain * 0.7,
                colorBlack,
                pow(ndist, 0.09)
            )
        );
    }
}
