mat2 rot(float a){return mat2(cos(a),-sin(a),sin(a),cos(a));}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 col;
    float t;

    for(int c=0;c<3;c++){
	    vec2 uv = (fragCoord*1.-iResolution.xy)/t;
        t = iTime+float(c)/100.;
        for(int i=0;i<5;i++)
        {
        	uv=abs(uv);
        	uv-=8.;
        	uv=uv*rot(t/float(i+1));
        }

     col[c] = fract(uv.x/t);

	}

    fragColor = vec4(vec3(col),1.0);

}
