void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec3 col = vec3(1.);
    float t = sin(iTime*.7)*.1+.8;

    uv *= 2.4;

    uv.y *= uv.y / t;
    uv.y += atan(t, iTime*t);
    uv.x /= uv.y;
    uv.x = fract(uv.x*t);

    col -= vec3(atan(uv.x*uv.x, uv.y*uv.y), atan(uv.y*uv.y, uv.x*uv.x), atan(uv.y*uv.y, uv.y*uv.y*t));
    col.r = col.g;
    col.g = 0.;

    uv = (fragCoord-.5*iResolution.xy)/iResolution.y;   
    col -= vec3(atan(uv.x*uv.x*t, uv.y*uv.y), atan(uv.y*uv.y, uv.x*uv.x), atan(uv.y*uv.y, uv.y*uv.y*t))*.7;
    col *= smoothstep(0.0, 0.271, length(uv.y));
    col.r *= 1.4;

    uv *= 4.;
    uv.y *= uv.y / t;
    uv.y += atan(t, iTime*t);
    uv.x /= uv.y;
    uv.x = fract(uv.x*t);

    col *= vec3(atan(uv.x*uv.x, uv.y*uv.y), atan(uv.y*uv.y, uv.x*uv.x), atan(uv.y*uv.y, uv.y*uv.y*t));

    uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float d = length(uv);
    float m = .0025/d;
    float c = m*pow(t, 5.);
    col.rg += vec2(c, c-.3);

    fragColor = vec4(col,1.0);
}
