#define N 6.0

void mainImage( out vec4 fragColor, in vec2 p )
{
    vec2 R = p + vec2(+1.0, 0.0);
    vec2 L = p + vec2(-1.0, 0.0);
    vec2 U = p + vec2(0.0, +1.0);
    vec2 D = p + vec2(0.0, -1.0);

    vec4 H = texture(iChannel0, L/iResolution.xy)
           - texture(iChannel0, R/iResolution.xy);
    vec4 V = texture(iChannel0, U/iResolution.xy)
           - texture(iChannel0, D/iResolution.xy);

    fragColor = floor(texture(iChannel0, p/iResolution.xy)*N)/N;
    fragColor.r *= 1.0 - 2.0 * length(vec2(H.r, V.r));
    fragColor.g *= 1.0 - 2.0 * length(vec2(H.g, V.g));
    fragColor.b *= 1.0 - 2.0 * length(vec2(H.b, V.b));
    fragColor.a *= 1.0 - 2.0 * length(vec2(H.a, V.a));
}
