const float radius = 80.0;

vec2 Center()
{
    float x = (sin(iTime) + 1.) / 2.;
    return vec2(x, 0.5);
}

float Spiral(vec2 fragCoord, vec2 center)
{
    if (true)
    {

        float angle = center.y / center.x;
        float offset = 0.;
        if(fragCoord.x < center.x)
        {
            if(fragCoord.y < center.y)
            {
                offset = 0.;
            }
            else
            {
                angle *= -1.;
                offset = center.y * 2.;
            }
        }
        else
        {
            angle = (center.y * 2. - center.y)/(center.x * 2. - center.x);
            if(fragCoord.y < center.y)
            {
                angle *= -1.;
                offset = 0.;
            }
            else
            {
                offset = 0.;
            }
        }
        float diff = angle * fragCoord.x + offset - fragCoord.y;
        if (diff > 0. && diff < 1.)
        {
            return 1.;
        }
    }
    return 0.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 center = Center() * iResolution.xy;

    if (distance(fragCoord, center) < radius) 
    {
        fragColor = texture(iChannel0, uv);
    }
    else
    {
		vec4 white = vec4(1, 1, 1, 1);
		vec4 col  = vec4(0, 0, 0, 0);
        col += Spiral(fragCoord, center) * white;
		fragColor = col;
    }
}
