vec2 cmul (vec2 c1, vec2 c2)
{
	float a = c1.x;
	float b = c1.y;
	float c = c2.x;
	float d = c2.y;

	return vec2 (a * c - b * d, b * c + a * d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = fragCoord.xy / iResolution.xy;
    //scaling real axis to [-2.5, 1.5] and  imaginary axis to  [-1.5, 1.5]/
    //Eyeballed coordinates to center the fractal
    c = c * vec2(4,3) - vec2(1.5, 1.5); 

    vec2 z = c;
    fragColor = vec4(0);

    int i = 0;

    for (;i<100;i++)
    {
        if (z.x * z.x + z.y * z.y >= 4.) 
        {
            break;
        }
       vec2 constant = vec2(0.85, 0.6);
       vec2 oneminusz = vec2(1, 0) - z;
       vec2 zTimesOneMinusZ = cmul(z, oneminusz);
       z = cmul(vec2(constant), zTimesOneMinusZ);
    }

    fragColor = vec4(i)/100.;

}
