void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = 5.*(fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    vec2 uv0 =  fragCoord.xy / iResolution.xy;
    float t = iTime;

    float x = uv.x;
    float y = uv.y;

    float m = 7.* cos(t+1.);
    float n = 5. * sin(t-4.); // m * (1. + t*t*t / 2.);

    float a = 4. * cos(t);
    float b = 2. * sin(t);

    float value = smoothstep(2. * abs(a * sin(m*x) * sin(n*y) + b * sin(n*x) * sin(m*y)),0.,1.);
	vec4 color = texture(iChannel0, uv0);

    fragColor = vec4(value *color);




}
