vec3 white = vec3(0.,0.,0.);
vec3 black = vec3(0.,0.,0.);
vec3 colorA = vec3(0.2509,0.1568,0.2901);
vec3 colorB = vec3(0.4509,0.2627,0.2941);
vec3 colorC = vec3(0.7019,0.3019,0.1450);
vec3 colorD = vec3(0.9411,0.4941,0.0274);
vec3 colorE = vec3(0.9686,0.8705,0.3333);


vec4 rectangle(vec2 uv, vec2 pos, float width, float height, vec3 color) {
	float t = 0.0;

	if ((uv.x > pos.x - width / 2.0) && (uv.x < pos.x + width / 2.0)
		&& (uv.y > pos.y - height / 2.0) && (uv.y < pos.y + height / 2.0)) {
		t = 1.0;
	}
	return vec4(color, t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
	vec2 center = vec2(0.1, 0.1);
	float width = .1;
	float height = .3;

    // Background layer
	vec4 bg = vec4(white, 1.0);

	// Rectangle
	vec3 col = black;
	vec4 rect = rectangle(uv, center, width, height, col);

	vec3 color = mix(colorA,colorB,uv.y);
    color = mix(color,colorC,uv.y);
	color = mix(color,colorD,uv.y);
    color = mix(colorE,color,uv.y);

    color = mix(colorA,color,sin(iTime + uv.y)) * mix(colorA,color,sin(iTime +  uv.y)) ;


    // Blend the two
	color = mix(color, black, rect.a);
    color = mix (color, black,rectangle(uv,vec2(.195,.1),.08,.2,col).a);
    color = mix (color, black,rectangle(uv,vec2(.28,.1),.08,.5,col).a);
    color = mix (color, black,rectangle(uv,vec2(.37,.1),.08,.4,col).a);
    color = mix (color, black,rectangle(uv,vec2(.455,.1),.08,.9,col).a);
    color = mix (color, black,rectangle(uv,vec2(.542,.1),.08,.7,col).a); 
    color = mix (color, black,rectangle(uv,vec2(.63,.1),.08,.8,col).a);  
    color = mix (color, black,rectangle(uv,vec2(.72,.1),.08,.5,col).a);  
    color = mix (color, black,rectangle(uv,vec2(.81,.1),.08,.5,col).a);

    color = mix (color, black,rectangle(uv,vec2(.90,.1),.08,.3,col).a);


    // Output to screen


    fragColor = vec4(color ,1.0);

}
