const vec4 BLACK = vec4(0.0, 0.0, 0.0, 1.0);
const vec4 WHITE = vec4(1.0,1.0,1.0,1.0);
const vec4 EMPTY_C = vec4(0.0);
const vec2 SIZE = vec2(32); // larger number = smaller dots

mat2 rotationMatrix2d(float time) {
  return mat2(cos(time), sin(time), -sin(time), cos(time));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 coord = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

  vec4 c = EMPTY_C;

  // still
  float total = floor(coord.x * float(SIZE.x)) +
                floor(coord.y * float(SIZE.y));
  bool isEven = mod(total, 2.0) == 0.0;
  c = isEven ? BLACK : WHITE;

  // rotating
  coord *= rotationMatrix2d(iTime * 0.01);
  float total2 = floor(coord.x * float(SIZE.x)) +
                 floor(coord.y * float(SIZE.y));
  bool isEven2 = mod(total2, 2.0) == 0.0;
  c *= isEven2 ? BLACK : WHITE;

  fragColor = c;
}
