vec2  center = vec2(0.85,0.5);
float radius = 0.15;
vec3  circColor = vec3(1.0,0.0,1.0);
vec3  background = vec3(1.0,0.5,0.3);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv.x *= iResolution.x / iResolution.y; 
    float t = abs(sin(iTime));
    //creates layers using colors
    vec4 layer1 = vec4(background, 1.0);
    vec4 layer2 = vec4(circColor, 0.0);

    // Linear 
     vec2 position = vec2(((1.0-t) * layer1) + ((t) * layer2));

    //step functions to change transparency
     if(length(position-uv) < radius)
        layer2.a = 1.0;  


    //mixes each layer
    vec4 mixed = mix(layer1, layer2, layer2.a);

    // Output to screen
    fragColor = mixed;
}
