const float r = 5.;
const int searchSize = int(r + velocityClip) - 1;

ivec2 ires = ivec2(0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(ires == ivec2(0))
        ires = ivec2(iResolution);


    ivec2 ic = ivec2(fragCoord);


    if(false)
    {
        vec4 ov = texelFetch(iChannel0, ic, 0);
        if(ov == NONE)
        {
            fragColor = vec4(0, 0, 0, 1);
            return;
        }
        else
        {
            fragColor = vec4(ov.xy/1., 0, 1);
            return;
        }
    }


    vec3 color = vec3(0);

    for(int x = ic.x - searchSize; x <= ic.x + searchSize; x++)
    {
        for(int y = ic.y - searchSize; y <= ic.y + searchSize; y++)
        {
            ivec2 ic = (ivec2(x, y) + ires) % ires;
            vec4 ov = texelFetch(iChannel0, ic, 0);

            if(ov != NONE)
            {
    			vec2 p = vec2(x, y) + ov.xy;

                float d = sq(fragCoord - p);

                color = max(vec3(smoothstep(sq(r), sq(r-1.), d)), color);
            }
        }
    }

    fragColor = vec4(color, 1);
}

ivec2 ires = ivec2(0);

const int twoR = 2*5;
vec4 timeStep(ivec2 ic, vec4 ov)
{
    vec2 p = vec2(ic) + ov.xy;

    vec2 a = vec2(0);

    for(int x = ic.x - twoR; x <= ic.x + twoR; x++)
    {
        for(int y = ic.y - twoR; y <= ic.y + twoR; y++)
        {        
            if(ivec2(x, y) == ic)continue;

            ivec2 oic = (ivec2(x, y) + ires) % ires;
            vec4 oov = texelFetch(iChannel0, oic, 0);

            if(oov == NONE)continue;

            vec2 op = vec2(x, y) + oov.xy;

            vec2 diff = p - op;
            float sqLen = sq(diff);

            if(sqLen <= sq(float(twoR)))
            {
                sqLen = max(0.5, sqLen);
                a += diff/pow(sqLen, 1.5);
            }
        }
    }

    //a = clamp(a, -velocityClip, velocityClip);

    ov.zw += 10.*a; 

    ov.zw = clamp(ov.zw, -velocityClip, velocityClip);

    ov.xy += dt*ov.zw;

    return ov;
}

vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(ires == ivec2(0))
        ires = ivec2(iResolution);


    ivec2 ic = ivec2(fragCoord);

    if(iFrame == 0)
    {
        ivec2 center = ires/2;
        ivec2 box = ivec2(500, 280);
        box /= 2;

        if((ic - center) % ivec2(20) == ivec2(0) && clamp(ic.x, center.x - box.x, center.x + box.x) == ic.x && clamp(ic.y, center.y - box.y, center.y + box.y) == ic.y)
        {
        	fragColor = vec4(0, 0, 1.*(2.*hash22(vec2(ic - center)/100.) - 1.));
        }
        else
        {
            fragColor = NONE;
        }

        return;
    }

    vec4 ov = texelFetch(iChannel0, ic, 0);

    if(ov != NONE)
    {        
        if(clamp(ov.xy, 0., 1.) != ov.xy)
        {
            fragColor = NONE;
        }
        else
        {
            fragColor = timeStep(ic, ov);
        }
    }
    else
    {
        for(int x = ic.x - maxThrow; x <= ic.x + maxThrow; x++)
        {
            for(int y = ic.y - maxThrow; y <= ic.y + maxThrow; y++)
            {
                ivec2 cic = (ivec2(x, y) + ires) % ires;
                vec4 cov = texelFetch(iChannel0, cic, 0);

                if(cov != NONE)
    			{
                    ivec2 p = cic + ivec2(floor(cov.xy));
                    p = (p + ires) % ires;

                    if(p == ic)
                    {
                        cov.xy -= vec2(ic) - vec2(x, y);

                        fragColor = timeStep(ic, cov);
                        return;
                    }
                }
            }
        }

        fragColor = NONE;
    }
}

#define NONE vec4(1000)

#define sq(x) dot(x, x)

const int maxThrow = 3;
const float dt = 1.;

const float velocityClip = float(maxThrow) / dt;
