void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv.x =uv.x+0.25*cos(iTime);
	vec3 ray = vec3(-1.0 + 2.0 * uv,-1);


    vec3 key = vec3(0.,1.,0.);
    vec3 jcvd = texture(iChannel0,uv).xyz;

    vec3 diff = jcvd - key;

    if(dot(diff,diff) > 0.6  && dot(diff,diff) < 2.0) 
    {

        fragColor = vec4(jcvd.xyz,1.);

    } else {

        fragColor = texture(iChannel1,ray);

    }


}
