float sine(in vec2 uv, in float y, in float blur, in float size) {
    return clamp(1.0-pow(fract(uv.y * 23.0) - y + size,blur), 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float amp = 0.3;
    float freq = 30.0;

    float x = uv.x;
    float y = amp * sin(x * freq * (cos(x) -.5) * uv.y / uv.x + iTime);

    float delta = -0.4;

    vec4 col = vec4(40./255., 17./255., 23./255., sine(uv, y*1.1, 1.5, 0.5 + delta));
    vec4 col2 = vec4(41./255., 58./255., 140./255., sine(uv, y*1.1, 0.5, -0.1 + delta));

    vec4 bgCol = vec4(93./255., 160./255., 166./255., 1.0);

    fragColor = bgCol;
    fragColor.rgb = mix(fragColor.rgb, col2.rgb, col2.a);
    fragColor.rgb = mix(fragColor.rgb, col.rgb, col.a);
}
