#define thickness 0.1
#define PI 3.1415

bool equals(float a, float b) {
    return (a - b < thickness);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.y;
	uv -= vec2(iResolution.x / iResolution.y / 2.0, 0.5);

    bool white = equals(mod(90.0 * uv.x, 4.0 * uv.y), (sin(mod(iTime, PI))+PI/2.0) * uv.y);

    fragColor = vec4(white,white,white,1.0);
}
