#define S(a, b, t) smoothstep(a, b, t)

// Config --------------------------
#define LINEWIDTH .02
#define SCALE 10.
#define DOTSIZE 12.
#define STARTSCOLOR vec3(.1, .6, 1.);
// ----------------------------------





float DistLine(vec2 p, vec2 a, vec2 b)
{
    vec2 pa = p-a;
    vec2 ba = b-a;
    float t = clamp(dot(pa, ba)/dot(ba, ba), 0., 1.);
    return length(pa - ba*t);
}

float N21(vec2 p)
{
    p = fract(p * vec2(215.33, 817.23));
    p += dot(p, p+ 25.24);
    return fract(p.x * p.y);
}

vec2 N22(vec2 p)
{
    float n = N21(p);
    return vec2(n, N21(p+n));
}

vec2 GetPos(vec2 id, vec2 offset)
{
    vec2 n = N22(id + offset) * iTime;
    return offset+sin(n)*.9;
}

float Line(vec2 p, vec2 a, vec2 b)
{
	float d = DistLine(p, a, b);
    float m = S(LINEWIDTH, .01, d);
    float d2 = length(a-b);
    m *= S(1.6, .3, d2)*.5 + S(.05, .01, abs(d2-.75));

    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5 *iResolution.xy) / iResolution.y;

    float m = 0.;
    uv *= SCALE;

    vec2 gv = fract(uv) - .5;
    vec2 id = floor(uv);

    vec2 p[9];
    int i = 0;
    for(float y=-1.; y<=1.; y++)
    {
        for(float x=-1.; x<=1.; x++)
    	{
    		p[i++] = GetPos(id, vec2(x, y));
        }
    }

    float t = iTime*10.;
    for (int i=0; i<9; i++)
    {
        m += Line(gv, p[4], p[i]);

        vec2 j = (p[i]-gv) * DOTSIZE;
        float sparkle = 1./ dot(j, j);
        m += sparkle * (sin(t+fract(p[i].x)*10.) * .5 + .5);
    }

    m += Line(gv, p[1], p[3]);
    m += Line(gv, p[1], p[5]);
    m += Line(gv, p[7], p[3]);
    m += Line(gv, p[7], p[5]);

    vec3 col = vec3(m);
    vec3 starsColor = STARTSCOLOR;
    col *= starsColor; 

    fragColor = vec4(col,1.0);
}
