#define gamma(v)    pow(v,1./2.2)
#define dither(v)   round((v)*N)/N

void mainImage( out vec4 O, vec2 U )
{
    float N = 1. + 62.*(iMouse.z<0. ? .5-.5*sin(.3*iTime) : iMouse.x/iResolution.x);

    U /= iResolution.xy;
    float v =   U.y > .67 ? dither(gamma(U.x))
              : U.y > .33 ? gamma(U.x)
              :             gamma(dither(U.x));
    O = vec4(v);

}
