#define R iResolution.xy
#define T iTime
#define TPW 3./R.y

float circle(in vec2 st, float r) {
	vec2 dst=st-vec2(.5);
    return 1.-smoothstep(r-TPW,r+TPW,
                         dot(dst,dst)*4.);
}
float box(in vec2 st, in vec2 sz){
    sz = vec2(0.5)-sz*0.5;
    vec2 uv = smoothstep(sz,sz+vec2(TPW),
                        st);
    uv *= smoothstep(sz,sz+vec2(TPW),
                    vec2(1.0)-st);
    return uv.x*uv.y;
}
float plus(in vec2 st, float sz){
    return  box(st, vec2(sz,sz/4.)) +
            box(st, vec2(sz/4.,sz));
}
mat2 rotate2d(float ang) {
	return mat2(cos(ang),-sin(ang),
                sin(ang),cos(ang));
}
void mainImage( out vec4 O, in vec2 U )
{
    vec2 uv = (2.*U-R)/R.y;
    vec3 col = vec3(0.);
    float fn=(tan(T)+3.)*.25;
    vec2 tr=vec2(fn);
    uv += tr*.5;

    uv -= vec2(.5);
    uv = rotate2d(50./fn)*uv;
    uv += vec2(.5);

    float crs=plus(uv,0.4);
    float crc=circle(uv,0.15);
    col += vec3(mix(crs,crc,step(.8,fn)));             
    O = vec4(col,1.0);
}
