void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    int i = 0;
    const int SquarCount = 5;
    float freq = 50.0;
    float yCal = iResolution.x/iResolution.y;
    float Fade = 1.0;

    vec2 CirCen0[9];
    CirCen0[0] = iMouse.xy/iResolution.xy;
    CirCen0[0] *= vec2(yCal,1.0); 
    CirCen0[1] = vec2(1,-1) * CirCen0[0];
    CirCen0[2] = CirCen0[1]+vec2(0.0,2.0);
    CirCen0[3] = vec2(-1,1) * CirCen0[0];
    CirCen0[4] = CirCen0[3]+vec2(2.0*yCal,0.0);

    float MyDist = 0.0;
    float Darkness = 0.0;
    float Adj = float(SquarCount);

    for(int i=0;i<SquarCount;i++)
    {
        MyDist = distance(CirCen0[i],uv);
        if (MyDist < Fade)
        {    
        	Darkness += (cos((MyDist*freq)-iTime*10.0)/(Adj))*(Fade-MyDist);
        }
    }

	Darkness = Darkness+0.5;
    fragColor = vec4(vec3(Darkness),1.0);
}
