uvec3 op(uvec3 a, uvec3 b, float method) {
    switch (int(method)) {
        //           sign                   exponent                    mantissa
        case 0:
    		return ((a & b) & (1u << 31)) | ((a & b) & (0xffu << 23)) | ((a ^ b) & 0x7fffffu);
        case 1:
    		return ((a & b) & (1u << 31)) | ((a ^ b) & (0xffu << 23)) >> 3 | ((a + b) & 0x7fffffu) << 8;
        case 2:
    		return ((a & b) & (1u << 31)) | ((a | b) & (0xffu << 23)) | ((a ^ ~b) & 0x7fffffu);
        case 3:
    		return ((a & b) & (1u << 31)) | ((a & b) & (0xffu << 23)) | ((a & b) & 0x7fffffu);
    }
    return uvec3(0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 a = texture(iChannel0, uv).rgb;
    vec3 b = texture(iChannel1, uv).rgb;
    vec3 color = uintBitsToFloat(op(floatBitsToUint(a), floatBitsToUint(b), round(uv.x) + round(uv.y) * 2.));

    fragColor = vec4(color, 1.);
}
