float circle(vec2 p, vec2 c, float r) {
    float d = distance(p, c);
    float w = 0.004;
    //return smoothstep(r-w, r, d) * (1.0-smoothstep(r, r+w, d));
    return smoothstep(r-w, r, d);
}

float line(vec2 p, vec2 s, vec2 d, float w) {
    vec2 dir = d - s;
    float th = atan(dir.x, dir.y);
    mat2 rot90 = mat2(cos(th), -sin(th),
              		  sin(th),  cos(th));
    vec2 dir1 = rot90 * dir;
    vec2 dir2 = -dir1;
    float y = abs((dir2.y-dir1.y)*p.x - (dir2.x-dir1.x)*p.y + dir2.x*dir1.y - dir2.y*dir1.x) / length(dir2-dir1);
    return 1.0-smoothstep(0.0, w, y);
    //return step(0.0-w, s.x) * (1.0-step(0.0, s.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float PI = 3.145926535;
	float TWOPI = PI * 2.0;

    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvc = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float angle = fract(atan(uvc.x, uvc.y)/TWOPI);

    float v = 0.0;
    for (float i=0.0; i<180.0; i+=1.0) {
    	v += 0.3 * line(uvc, vec2(0.0), vec2(cos(radians(i)), sin(radians(i))), 2.0/iResolution.x);
        v += 0.1 * line(uvc, vec2(0.0), vec2(cos(radians(i)), sin(radians(i))), 5.0/iResolution.x);
        v += 0.05 * line(uvc, vec2(0.0), vec2(cos(radians(i)), sin(radians(i))), 8.0/iResolution.x);
    }
    float amp = fract(texture(iChannel0, vec2(angle), 0.5).x);
    v *= smoothstep(0.1, 0.2, distance(uvc*amp*0.55, vec2(0.0))+(1.0-step(0.08, distance(uvc*amp*0.55, vec2(0.0)))));
    //if (step(0.9, 0.5+0.5*sin(720.0*PI*angle)) == 1.0) {
    	//float fv = texture(iChannel0, vec2(angle), 0.5).x;
    	//v = circle(uvc, vec2(0.0), 0.3 * fv);    
    //}
    fragColor = vec4(vec3(0.0,0.1,0.2)+vec3(0.5*v,0.65*v,0.8*v),1.0);
}
