void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 6.28 - 3.14;

	float baseLine = cos(uv.x + iTime);
    float bubble = sin(uv.y*.5  + iTime * 3.) * 0.2 + 0.8;


    float banding = fract((uv.y + baseLine) * bubble);


    vec3 green = vec3(0.0, 0.36862, 0.36470);
    vec3 darkGreen = green * 0.5;

    vec3 color = mix(green, darkGreen, banding);

    // Output to screen
    fragColor = vec4(color, 1.0);
}
