//adjustable values
float numRows = 5.0;
float numCols = 5.0;

vec3 colorOne = vec3(0.0, 0.0, 0.0);
vec3 colorTwo = vec3(1.0, 1.0, 1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Aspect ratio set and fix
	float aspectRatio = iResolution.x/iResolution.y;
	uv.x *= aspectRatio;

    //modify uv
    uv.y *= numCols;
    uv.x *= numRows;

    //Make checkerboard
    float mixNum = mod((floor(uv.y) + floor(uv.x)), 2.0);
    uv = fract(uv);

    //layers
    vec4 layerOne = vec4(colorOne, 0.0);
    vec4 layerTwo = vec4(colorTwo, 0.0);
    layerTwo.a = 1.0 - mixNum;

    //Compile layers
    vec4 composite = mix(layerOne, layerTwo, layerTwo.a);

    uv = fragCoord/iResolution.xy;
    vec4 layerThree = texture (iChannel0, uv);
    vec4 layerFour = texture (iChannel1, uv);
    composite = mix (layerThree, layerFour, composite.a);


    // Output to screen
    fragColor = composite;
}
