void mainImage(out vec4 fragColor,in vec2 fragCoord)
	{
    // sample the texture
    vec2 uv = fragCoord/iResolution.xy;
    vec4 col = texture(iChannel0,uv);

    // gamma correction
    col = vec4(pow(col.rgb,vec3(2.2)) - 0.004,col.a);

    // find bayer matrix entry based on fragment position
    vec2 uv_bayer = vec2((fragCoord-floor(fragCoord/8.0)*8.0)/8.0);
    float bayerValue = texture(iChannel1,uv_bayer).r;

    // output
    if (fragCoord.x > iMouse.x)
    	{
        fragColor = vec4(
            step(bayerValue,col.r),
            step(bayerValue,col.g),
            step(bayerValue,col.b),
            col.a);
        }
    else
        fragColor = col;
	}
