void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 uvc = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float t = iTime;
    uvc *= 9.678;

    float c = 0.0;
    float v1 = cos(uvc.x+sin(t*1.231234))+sin(1.4239234*uvc.y+t);
    float v2 = sin(sin(uvc.x)+t)+sin(sin(2.4239234*uvc.y)+t);
    float v3 = sin(3.3238*sin(distance(uv, uvc)+t/0.3));
    c = sin(v1 + v2 + v3) * 0.8 + 0.5;

    float res = 0.0;
    for (int i=1; i<=3; i++) {
        float l = float(i);
        float w = 0.02;
    	res += l < c && c < l + w ? 1.0 : 0.0;
    }

    fragColor = vec4(vec3(res), 1.0);
}
