void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 col = texture(iChannel1,vec2(uv.xy),0.0).rgb;
    vec2 center = iMouse.xy;
    vec2 position  = uv - vec2(iMouse.x/(iResolution.x ),iMouse.y/iResolution.y);//iMouse.xy/iResolution.xy ;
    float ratio = iResolution.x /iResolution.y;
    float radius = 0.0005;
    //float aux=  1.- step(.0005,position.x*position.x*ratio +position.y*position.y/ratio);
    float aux=  1.- smoothstep(radius,radius*1.3,position.x*position.x*ratio +position.y*position.y/ratio);

    col +=vec3(aux);
    fragColor = vec4(vec3(col),1.0);
}
