void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    uv *= 5.;


    vec2 gv = fract(uv * 1.);

    vec3 col = vec3(0.);

    for(float x = -1.; x <= 1.; x+=1.){
         for(float y = -1.; y <= 1.; y++){

            vec2 offs = vec2(x,y);
            vec2 center = floor(uv)+0.5 + offs;

    		float r = smoothstep(.3, 0.25, length(uv + 0.5 * sin(iTime*2.) - center));    
    		float g = smoothstep(.3, 0.25, length(uv + 1.0 * cos(iTime)    - center));    
    		float b = smoothstep(.65, 0.25, length(uv - center));

    		col += vec3(r, g, b);
         }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
