#define CIRCLES 3.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.);

    // middle circle
    float d = length(uv);
    float middleCircle = smoothstep(0.102, 0.1, d);
    float middleGradient = smoothstep(0.14, 0.0, d);
    col.r += middleCircle;
    col.rgb += middleGradient*.19;

    // satellites
    for(float i=1.; i<=CIRCLES; i++) {
    	float dist = .1 + 0.085 * i;
        float t = (iTime*i*fract(i*10.900))/2.-i*10.;

        // circles
    	vec2 s = vec2(length(uv.x-dist*sin(t)), length(uv.y-dist*cos(t)));
        float circle = smoothstep(0.041, 0.04, length(s));
    	col += circle; // white circle
        float grad = smoothstep(0.048, 0.000, length(s)); // circle gradient

        // lines
        float lin = (smoothstep(dist-0.002, dist+0.001, length(uv)))*.9;
        float lout = (smoothstep(dist+0.002, dist+0.003, length(uv)))*.9;
        vec3 line = vec3(lin - lout);
        line -= circle;
        line *= vec3(.07, .07, .07);   

        // colors
        if(i==1.) {    
            col.b -= circle;
            col += clamp(line, 0., 1.);
            col += grad*.24;
        }
        if(i==2.) {
            col.rg -= circle*.7;
            col += clamp(line, 0., 1.);
            col += grad*.05;
        }
        if(i==3.) {
            col.g -= circle;
            col.b -= circle*0.5;
            col += clamp(line, 0., 1.);
            col += grad*.18;
        }
        if(i>3.) {
            col -= circle*.7;
        }
    }

    fragColor = vec4(col,1.0);
}
