// BASIC RAY-MARCHING CLASS DEMO from Doug James : https://www.shadertoy.com/view/wdKyD3
//
// Numerous methods borrowed from iq articles:
//   https://iquilezles.org/www/index.htm
// 
// 3D signed distance functions:
//   https://iquilezles.org/articles/distfunctions
// 
// Shadows:
//   https://iquilezles.org/articles/rmshadows
// 
// Ray-marching SDFs:
//   https://iquilezles.org/articles/raymarchingdf
// 
// Normals for SDFs:
//   https://iquilezles.org/articles/normalsSDF

float opUnion( float d1, float d2 ) {  return min(d1,d2); }
vec4  opUnion(vec4 d1, vec4 d2) {
 return (d1.x < d2.x ? d1 : d2);   
}

float opIntersection( float d1, float d2 ) { return max(d1,d2); }
vec4  opIntersection( vec4  d1, vec4  d2 ) {
 return (d1.x > d2.x ? d1 : d2);   

}

// subtracts d1 from d2
float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }
vec4  opSubtraction( vec4  d1, vec4  d2 ) { 
  return opIntersection(vec4(-d1.x,d1.yzw), d2);
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }

float opSmoothIntersection( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h); }

float opOnion( in float sdf, in float thickness )
{
    return abs(sdf)-thickness;
}

// 3D gradient noise methods. 
// https://www.shadertoy.com/view/Xsl3Dl
vec3 hash( vec3 p ) // replace this by something better
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}
float noise( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
                          dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
                          dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
                mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
                          dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
                          dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}
const mat3 m = mat3( 0.00,  0.80,  0.60,
                    -0.80,  0.36, -0.48,
                    -0.60, -0.48,  0.64 );
float fbm( in vec3 pos )
{
    vec3  q = 8.0*pos;
    float f = 0.5000*noise( q ); q = m*q*2.01;
    f += 0.2500*noise( q ); //q = m*q*2.02;
    //f += 0.1250*noise( q ); q = m*q*2.03;
    //f += 0.0625*noise( q ); 
    return f;
}




vec3 rotate_y(vec3 v, float angle)
{
	float ca = cos(angle); float sa = sin(angle);
	return v*mat3(
		+ca, +.0, -sa,
		+.0,+1.0, +.0,
		+sa, +.0, +ca);
}
vec3 rotate_x(vec3 v, float angle)
{
	float ca = cos(angle); float sa = sin(angle);
	return v*mat3(
		+1.0, +.0, +.0,
		+.0, +ca, -sa,
		+.0, +sa, +ca);
}
vec3 rotate_z(vec3 v, float angle)
{
	float ca = cos(angle); float sa = sin(angle);
	return v*mat3(
		+ca, -sa, +.0,
		+sa, +ca, 0.,
		+.0, +.0, 1.);
}

// iq distance functions, 
// See https://iquilezles.org/articles/distfunctions
float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }
float ndot( in vec2 a, in vec2 b ) { return a.x*b.x - a.y*b.y; }

float sdSphere(vec3 p, float radius )
{
  return length(p)-radius;
}

float sdTorus(in vec3 p, in vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}
float sdCappedCone( vec3 p, float h, float r1, float r2 )
{
  vec2 q = vec2( length(p.xz), p.y );
  vec2 k1 = vec2(r2,h);
  vec2 k2 = vec2(r2-r1,2.0*h);
  vec2 ca = vec2(q.x-min(q.x,(q.y<0.0)?r1:r2), abs(q.y)-h);
  vec2 cb = q - k1 + k2*clamp( dot(k1-q,k2)/dot2(k2), 0.0, 1.0 );
  float s = (cb.x<0.0 && ca.y<0.0) ? -1.0 : 1.0;
  return s*sqrt( min(dot2(ca),dot2(cb)) );
}
float sdTriPrism( vec3 p, vec2 h )
{
  vec3 q = abs(p);
  return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}
float sdPlane( vec3 p, vec3 n, float h )
{
  // n must be normalized
  return dot(p,n) + h;
}
float sdBoundingBox( vec3 p, vec3 b, float e )
{
  p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

// not exact
float sdEllipsoid( vec3 p, vec3 r )
{
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}

// See https://www.shadertoy.com/view/Wdjfz3
float sdEgg( in vec2 p, in float ra, in float rb )
{
    const float k = sqrt(3.0);

    p.x = abs(p.x);

    float r = ra - rb;

    return ((p.y<0.0)       ? length(vec2(p.x,  p.y    )) - r :
            (k*(p.x+r)<p.y) ? length(vec2(p.x,  p.y-k*r)) :
                              length(vec2(p.x+r,p.y    )) - 2.0*r) - rb;
}
float sdCappedTorus(in vec3 p, in vec2 sc, in float ra, in float rb)
{
  p.x = abs(p.x);
  float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
  return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}
float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float sdVerticalCapsule( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}

vec3 bendPoint( in vec3 p , float amt)
{
    float k = amt; //  some amount, e.g., 10.
    float c = cos(k*p.x);
    float s = sin(k*p.x);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*p.xy,p.z);
    return q;
}
// Function to y-axis revolve a 2D SDF into a 3D SDF.
vec2 revolvePoint( in vec3 p, float radius )
{
    return vec2( length(p.xz) - radius, p.y );
}

// Simple ellipsoidal SDF pumpkin at origin.
float sdPumpkin(vec3 p) 
{
	float sdf = 100.;
    for(int i=0; i<10; i++) {// somewhat expensive
    	vec3  rp = rotate_y(p, 6.28*float(i)/10.+0.27);
        vec3  q  = rotate_z(rp-vec3(0.6,0.,0.),-0.1);
	    float d  = sdEllipsoid(q, vec3(0.8, 1., 0.6));
        sdf = opSmoothUnion(d, sdf, 0.03);
        //sdf = opUnion(d, sdf);
    }

    return sdf;
}


/// SCENE PARAMETERS (feel free to add your own):
const vec3 cactusCenter = vec3(0., -0.15, -8.0);
const vec3 Cd_pumpkinOutside = vec3(231./255., 111./255., 3./255.); 
const vec3 Cd_pumpkinInside  = vec3(1.,0.7,0.);
const vec3 Cd_stem           = vec3(0.35,0.25,0.15);
const vec3 Cd_cactusOutside  = vec3(19./255., 168./255., 19./255.);

// SDF implementation for our scene. 
// Returns 4 values: (sdf, Cd)
//   .x: SDF distance, 
//   .y: Cd.r
//   .z: Cd.g
//   .w: Cd.b
// NOTE: You could return other things for yzw, e.g., y:objectID int to set more colors in "render."
vec4 mapV4(in vec3 p)
{
    // SDF vector (sdf, r,g,b) to be returned:
    vec4 sdf = vec4(100000., -1., 0., 0.);

    float mx = iMouse.x/iResolution.x;

    // TODO: REPLACE THE FOLLOWING WITH YOUR SCENE CODE:

    // Translate coordinate to pumpkin center:
    vec3 c = cactusCenter;
    p -= c;

    {// BODY:
		// OUTSIDE: Union of ellipsoids
        vec3 q = p;//local copy
        //sdf.x = sdPumpkin(q);
        sdf.x = sdVerticalCapsule(q + vec3(0., 1.5, 0.), 2.5, 0.8);
        sdf.yzw = Cd_cactusOutside;// + 0.1*vec3(1.,1.,0.)*length(q.xz);

    }
    {// BRANCH:
        vec3 q = p;
        q.y *= -1.;
        //right branch
        float branchSD = sdCappedTorus(q - vec3(1.,0.,0.), vec2(1., 0.5), 0.31, 0.25);
        vec4 branch = vec4(branchSD, Cd_cactusOutside);
        sdf    = opUnion(sdf, branch);
    }

    {//STEM:
        vec3 q = p; 
        q.y *= -1.;
        vec3 tmpq = q;
	    q.yxz = bendPoint(q.yxz, 0.4);
        q = rotate_z(q, 0.4);
    	float stemSD  = sdCappedCone(q + vec3(.0, 1.25,0.), 0.45, 0.1, 0.2);// (p, h, r1, r2)
        vec3  stemCol = Cd_stem*2.;
        if(stemSD<0.01){// modify color
	        float theta = atan(q.z, q.x);// + 0.015*noise(10.*p);
            stemCol.xy += 0.02*abs(sin(40.*theta)-0.9)*abs(sin(100.*theta)-0.9);
            //stemSD *= (1. + 1.*abs(sin(10.*theta)-0.9));
        }

        vec4  stem = vec4(stemSD, stemCol);
    	sdf   = opUnion(sdf, stem);
    }

    {// ADD NOISE:  (use sparingly--ruins signed-distance property)
//    	sdf += 0.006*noise(8.*p);
        //sdf.x += 0.015*fbm(0.65*p); // costly. 
    }

    {// CARVE:
		// NOSE:
        float noseSD = sdTriPrism(p-vec3(0.,0.,1.2), vec2(.2, 1.));
        vec4  noseCutout = vec4(noseSD, Cd_pumpkinInside);
        sdf = opSubtraction(noseCutout, sdf);

        // EYES:
        vec2 xyEye = vec2(0.45, 0.35);
        float eyeSD = sdCapsule(vec3(abs(p.x),p.yz), vec3(xyEye, 0.), vec3(xyEye, 2.), 0.2);
        vec4  eyeCutout = vec4(eyeSD, Cd_pumpkinInside);
        sdf = opSubtraction(eyeCutout, sdf);


        // MOUTH: 
        //vec3  q = vec3(p.x, -p.y+0.5, p.z-.5);
        float d = sdRoundBox(p + vec3(0.,0.5, -0.8), vec3(0.5, 0.01, 0.8), 0.1);
            //sdCappedTorus(q, vec2(0.866025,-0.5), 0.55, 0.05);//vec2(0.8, 0.0), 1., 0.2);
        vec4  mouth = vec4(d, Cd_pumpkinInside);
        sdf = opSubtraction(mouth, sdf);


    }


    // CUTAWAY PLANE:
    #if 0
        float d = sdPlane( p, normalize(vec3(-1.,0.,-1.)), 3.*(mx-0.5));
        vec4 cutPlane = vec4(d, Cd_pumpkinInside);
        //sdf = opIntersection(sdf, cutPlane); 
        sdf = opSubtraction(cutPlane, sdf); 
    #endif

    #if 0   
    {// COORDINATE BOX GUIDE:
    	float d = sdBoundingBox(p, vec3(1.), 0.02);
        sdf = opUnion(sdf, vec4(d, vec3(0.8)));
    }
    #endif 

    return sdf;
}


// Scalar SDF implementation for our scene
float map(in vec3 p)
{
    return mapV4(p).x;// just sdf value
}

