void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float col;
    float dim=512.;
    vec2 uv = fragCoord/iResolution.xy;
    uv*=dim;
    if ((int(uv.x) ^ int(uv.y))<(int(59.*iTime)%int(dim))){
      col=1.;   
    }
    fragColor = vec4(col,.5+.2*sin(col*iTime+uv.y/20.),.5+.2*cos(col*iTime+uv.x/20.),1.);
}
