float dmd(vec2 uv, vec2 p, float size, float intensity){
    uv -= p;
	float m = mix(2.*abs(uv.x) + abs(uv.y),length(uv),.4);
    float shp = m;
    shp = smoothstep(size + .02,size,m);
    shp -= smoothstep(size  ,size - .02,m);
	shp += intensity*smoothstep(size - .019  ,size - .05,m);
    //m -= smoothstep(size -.1 ,size -.2,m);
    shp += .1*smoothstep(size + .3,size - .1, length(uv));
	return clamp(shp,0.,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = 2.*fragCoord/iResolution.xy-1.;
	uv.x *= iResolution.x/iResolution.y;
	vec2 cursor = 2.*iMouse.xy/iResolution.xy-1.;
	cursor.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);
    col += 2.*(1.-abs(uv.y))*vec3(.1,.05,.2);

    float innerCircle = smoothstep(.35, .43, length(uv));

    col += .1*vec3(.1, .2, .8)*smoothstep(-.01, .01, sin(uv.x*12.*cos(uv.y) + iTime)*cos(uv.y*12. - iTime*.3));
    col += vec3(.8, .0, .4)*smoothstep(.42, .41, length(uv)+.015*sin(uv.x*34.*cos(uv.y) - iTime)*cos(sin(iTime) + uv.y*34. - iTime*.3));

    col *= innerCircle;



    float asp = iResolution.x/iResolution.y;
	for (float i = -3.1415; i <= 3.1415; i+=.35) {
		vec2 pos = vec2(.5*cos(.1*iTime+i),.8*sin(1.*iTime+i));
        float scale = .4 + .15*sin(1.*iTime+i+1.57);
        float blink = fract(i*10.);
        vec3 color = vec3(.5+.5*sin(iTime-i+2.),.5+.5*sin(iTime-i-2.),.5+.5*sin(3.*iTime-i-.4));
        //scale = .5;
        if (scale >= .3)
    		col += color*dmd(uv, pos, scale*.3, blink);
        else {
            col += color*dmd(uv, pos, scale*.3, blink)*innerCircle;
            //col *= innerCircle;
        	//col = vec3(smoothstep(.4, .41, length(uv)));
        }

        //if (scale <= .3) col *= 0.; //smoothstep(.4, .41, length(uv));
    }


    //col *= smoothstep(.3, .31, length(uv));



    // Output to screen
    fragColor = vec4(col,1.0);
}
