float circle(vec2 _st)
{
    return length(1.0 - _st);
}


float cloud(vec2 _st)
{
    float cloudReturn = smoothstep(circle(_st - vec2(0.10, 0.05))-0.01, circle(_st - vec2(0.10, 0.05))+0.01, 0.075);

    float tmp = smoothstep(circle(_st - vec2(0.02, 0.0))-0.01, circle(_st - vec2(0.02, 0.0))+0.01, 0.052);    
    tmp += smoothstep(circle(_st - vec2(0.15, -0.01))-0.01, circle(_st - vec2(0.15, -0.01))+0.01, 0.075);    
    tmp += smoothstep(circle(_st - vec2(0.080, -0.02))-0.01, circle(_st - vec2(0.080, -0.02))+0.01, 0.05);
    cloudReturn += tmp - (tmp*cloudReturn);

 	return cloudReturn;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st       = 2.0 * fragCoord.xy / iResolution.x + vec2(0.5, 0.95);

    vec3 col = vec3(1.0, 1.0, 0.0);
    col = mix(vec3(1.0, 0.549, 0.0), vec3(1.0, 0.1, 0.0),  vec3(st.y - 0.95));    

    float circleDF = smoothstep(circle(st - vec2(0.5, 0.0))-0.5, circle(st - vec2(0.5, 0.0))+0.5, 0.5);
    col = mix(vec3(circleDF) * vec3(1.0, 1.0, 0.0), col, 1.0-circleDF);    

    float cloudDF = cloud(st - vec2(-0.35, 0.85));         
    cloudDF += cloud(st - vec2(0.35, 0.9));   
    cloudDF += cloud(st - vec2(0.5, 0.62));       
    cloudDF += cloud(st - vec2(0.7, 0.55));    
    cloudDF += cloud(st - vec2(1.2, 1.1));   
    cloudDF += cloud(st - vec2(1.1, 0.85)); 

    col = mix(vec3(cloudDF) * vec3(0.0), col, 1.0-cloudDF);  

    circleDF = smoothstep(circle(st - vec2(-0.2, -0.25))-0.01, circle(st - vec2(-0.2, -0.25))+0.015, 0.5);   
    circleDF += smoothstep(circle(st * vec2(1.5, 1.0) - vec2(0.6, -0.3))-0.01, circle(st * vec2(1.5, 1.0) - vec2(0.6, -0.3))+0.01, 0.5);   
    circleDF += smoothstep(circle(st * vec2(0.5, 1.075) - vec2(0.0, -0.3))-0.01, circle(st * vec2(0.5, 1.075) - vec2(0.0, -0.3))+0.01, 0.5);
    col = mix(vec3(circleDF) * vec3(0.0), col, 1.0-circleDF);

    fragColor = vec4(col,1.0);
}
