float maxIt = 5.0; 


float IsInSet(vec2 uv)
{
   float a = 0.0;
   float b = 0.0;

   float ca = uv.x;
   float cb = uv.y;

   float i = 0.0;

   while(i<=maxIt)
   {
       float an = a*a-b*b+ca;
       float bn = 2.0*a*b+cb;

       a = an;
       b = bn;

       float val = abs(a*b);

       if(val>=8. && val<=16.) break;

       i++;
   }

   return i+maxIt/3.;
}

vec3 GetColor(vec2 uv)
{
    float c = 1.0-IsInSet(uv)/maxIt;

    float r;
    float g;
    float b;

    if(c>0. && c <=0.5)                   r =  sin(c*2.0*3.1415); 
    if(c>=1.0/4.0 && c<= 3.0/4.0) g = sin((c-1.0/4.0)*2.0*3.1415);    
    if(c >=0.5)                   b = sin((c-0.5)*2.0*3.1415);

    return vec3(r,g,b);
 }


vec3 Gradient(vec2 uv)
{ 
    float c = uv.x;
    float r;
    float g;
    float b;

    if(c>0. && c <=0.5)                   r =  sin(c*2.0*3.1415); 
    if(c>=1.0/4.0 && c<= 3.0/4.0) g = sin((c-1.0/4.0)*2.0*3.1415);    
    if(c >=0.5)                   b = sin((c-0.5)*2.0*3.1415);

    return vec3(r,g,b);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    maxIt = iTime;
    vec2 uv = ((fragCoord/(.5*iResolution.y))-vec2(2,1))*1.5;

    vec3 col = GetColor(uv);

    //col = Gradient(fragCoord/iResolution.xy);
    fragColor = vec4(col,1.0);
}
