float timeScale = 0.5;
float pi = 3.14;

float fracturedTime(float offset){
    float _fractured = fract(iTime*timeScale + offset);
    _fractured = distance(_fractured, 0.5) * 2.;
    _fractured = 1. - _fractured;
    return _fractured;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord/iResolution.xy;
    float coord = abs(cos(distance(xy.x, 0.5)*pi))*abs(cos(distance(xy.y, 0.5)*pi));
    vec4 texColor = vec4(
        0.,
        fracturedTime(0. + coord),
        fracturedTime(0. + coord) * 0.77,
        1.0);
    fragColor = texColor;

}
