void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    uv.x = (uv.x + 0.5 * cos(iTime)) * sin(iTime);
    uv.y = (uv.y + 0.5 * sin(iTime)) * cos(iTime);
    vec4 color = texture(iChannel0, uv);

    color.r = color.r * abs(cos(iTime));
    color.g = color.g * abs(cos(iTime * 3.0));
    color.b = color.b * abs(sin(iTime));

    fragColor = color;
}
