/*
    The Mandelbrot set is defined as the set of points c in the complex plane to which the 
    recursively defined sequence:
        z_0 = 0
        z_{n+1} = z_n*z_n + c
    it does not tend to infinity.
*/

vec2 multiply(vec2 a,vec2 b){
    if(a.x==0.){
        if(b.x==0.)
            return vec2(-a.y*b.y,0.);
        else
            return vec2(-a.y*b.y*sign(b.x),a.y*abs(b.x));
    }
    else{
        if(b.x==0.)
            return vec2(-a.y*b.y*sign(a.x),b.y*abs(a.x));
        else{
            return vec2	(a.x*b.x-a.y*b.y*sign(a.x*b.x),abs(a.x)*b.y+a.y*abs(b.x));
        }
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 3.*(fragCoord/iResolution.x-vec2(.7,.27));
    vec2 col;

    vec2 z0 = vec2(0.2*cos(iTime),0.2*sin(iTime));
    for(int i = 0; i < 1000;i++){
        vec2 z = multiply(z0,z0)+uv;
        float mq = dot(z,z);
        if( mq > 1000.0){
            col = vec2(float(i)/30.0,0.0);
            break;
        } else {
            z0 = z;
        }
        col =  vec2(mq,mq);
    }
    fragColor = vec4(col,0.0,1.0);
}
