// Hardcoded 4 Color Stops as a demonstration.
vec4 color0 = vec4(1,0,0,1);
vec4 color1 = vec4(0,0,1,1);
float colorStop0 = 0.0;
float colorStop1 = 1.0;

// Starting and ending points
float sx = 50.0;//cordinate of the solid red end on the x axis
float sy = 50.0;//cordinate of the solid red end on the y axis
float ex = 300.0;//cordinate of the solid blue end on the x axis
float ey = 350.0;//cordinate of the solid blue end on the y axis

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 start = vec2(sx, iResolution.y - sy);
    vec2 end = vec2(ex, iResolution.y - ey);

    vec2 dt = end - start;
    vec2 pt = fragCoord - start;
    float t = dot(pt, dt)/ dot(dt, dt);

    vec4 color;
    //color0(1-t)+ color1(t)
    color = mix(color0, color1, clamp((t - colorStop0)/(colorStop1 - colorStop0), 0.0, 1.0));
    fragColor = color;
}
