vec3 hsvToRgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

const float pi = 3.1415926535897932384;

mat2 rotation ( in float _angle ) {
    return mat2(
        cos(_angle), sin(_angle),
    	-sin(_angle), cos(_angle)
    );
}

float angle(in vec2 pos) {
    if (abs(pos.x) < 0.0001) {
        if (pos.y >= 0.0) {
            return pi / 2.0;
        }
        if (pos.y < 0.0) {
            return -pi / 2.0;
        }
    }
    return atan(pos.y, pos.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float time = iTime * 0.75;

    vec2 uv0 = (fragCoord  - 0.5* iResolution.xy ) / iResolution.y;

    vec2 uv = rotation(pi * 0.75) * uv0;

    float theta = angle(uv) * 5.2 / pi;

    float uvrX = 19.0 * pow(
        length(uv),
        0.8
    );

    vec2 uvr = vec2(
        uvrX,
        theta * 5.0 + 3.2 * sin(uvrX + 4.0 + time)
    );

    vec3 color1 = hsvToRgb(vec3(
        0.79,
        0.70,
        0.65
    ));

    vec3 color2 = hsvToRgb(vec3(
        0.9,
        0.85,
        0.35
    ));

    // Inside gradient
    if (uvr.x < 7.0) {
        fragColor = vec4(mix(
            color1,
            color2,
            0.5 + 0.5 * sin(uvr.x * 0.5 + uvr.y * 0.2)
        ), 1.0);
        return;
    }

    fragColor = vec4(mix(
        vec3(0.12, 0.1, 0.15) * 1.05,
        vec3(0.05) * 1.5,
        0.5 + 0.5 * sin(
            -uv0.x * 10.0 +
            -uv0.y * 10.0 +
            -15.0 * uv0.x * uv0.y +
            -time * 0.5
        )
    ), 1.0);
}
