void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    int liveNeighbors = 0;
    int selfState = 0;
    for (int i=-1; i < 2; i++) {
        for (int j=-1; j < 2; j++) {
            int state = int(texelFetch(iChannel0, ivec2(fragCoord + vec2(i, j)), 0).x);
            if (i == 0 && j == 0) {
                 selfState = state;
            }

            else {
                liveNeighbors += state;
            }
        }
    }

    if (selfState == 1) {
        if (liveNeighbors == 3) {//8
            selfState = 0;
        }
    }

    else {
        if (liveNeighbors < 2) {
            selfState = 0;
        }

        if (liveNeighbors == 2 || liveNeighbors == 3) {
            selfState = 0;
        }

        if (liveNeighbors > 3) {//3
            selfState = 1;
        }
    }

    if (length(iMouse.xy - fragCoord) < 10.0) {
        selfState += 1;
    }

    fragColor = vec4(vec3(selfState), 1.0);
}
