void mainImage( out vec4 fragColor, in vec2 FC )
{
    vec2 uv = (FC*2.-iResolution.xy)/iResolution.y;
     //d -расстояние от камеры
    //e - безопасное расстояние от пути d 
    float e,d=0.;
    vec3 p,rd = vec3(uv,1);
    int i;
    for(int i = 2;i<9;i++){
        p=rd*d - vec3(0.,0.,2.);
        d+=e=length(p-vec3(0.,0.,sin(iTime*3.)))-0.5;
        if(e<.01)break;
    }
    vec3 col = cos(iTime+uv.xyx+vec3(0,2,4)) + 0.98*cos(iTime+uv.xyx+vec3(0,2,4))/d;
    fragColor = vec4(vec3(col),0.);
}
